contracts/GameDinL3Bridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title GameDin L3 Bridge Contract
 * @dev Handles cross-chain transfers between L2 settlement layer and L3 gaming network
 * Manages asset bridging, validation, and cross-chain communication
 */
contract GameDinL3Bridge is AccessControl, ReentrancyGuard, Pausable {
    // =============================================================================
    // CONSTANTS & ROLES
    // =============================================================================
    
    bytes32 public constant BRIDGE_OPERATOR_ROLE = keccak256("BRIDGE_OPERATOR_ROLE");
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");
    
    uint256 public constant BRIDGE_TIMEOUT = 24 hours;
    uint256 public constant MAX_BRIDGE_AMOUNT = 100000 * 10**18; // 100K tokens
    uint256 public constant MIN_BRIDGE_AMOUNT = 1 * 10**18; // 1 token
    
    // =============================================================================
    // STRUCTS & ENUMS
    // =============================================================================
    
    enum BridgeStatus {
        PENDING,
        PROCESSING,
        COMPLETED,
        FAILED,
        CANCELLED
    }
    
    enum AssetType {
        TOKEN,
        NFT,
        ETH
    }
    
    struct BridgeRequest {
        uint256 requestId;
        address sender;
        address recipient;
        AssetType assetType;
        address assetAddress;
        uint256 amount;
        uint256 tokenId;
        BridgeStatus status;
        uint256 timestamp;
        uint256 deadline;
        bytes32 merkleRoot;
        string l3TransactionHash;
        address validator;
        bool isL3ToL2;
    }
    
    struct BridgeConfig {
        bool isActive;
        uint256 minConfirmations;
        uint256 maxAmount;
        uint256 fee;
        uint256 timeout;
    }
    
    struct ValidatorInfo {
        bool isActive;
        uint256 totalProcessed;
        uint256 successRate;
        uint256 lastActivity;
        uint256 stake;
    }
    
    // =============================================================================
    // STATE VARIABLES
    // =============================================================================
    
    address public settlementContract;
    uint256 public requestCounter;
    uint256 public totalRequests;
    uint256 public totalVolume;
    uint256 public bridgeFee = 0.001 * 10**18; // 0.001 tokens
    uint256 public validatorStake = 1000 * 10**18; // 1000 tokens
    
    mapping(uint256 => BridgeRequest) public bridgeRequests;
    mapping(address => uint256[]) public userRequests;
    mapping(address => ValidatorInfo) public validators;
    mapping(bytes32 => bool) public processedTransactions;
    mapping(address => BridgeConfig) public bridgeConfigs;
    
    // =============================================================================
    // EVENTS
    // =============================================================================
    
    event BridgeRequestCreated(
        uint256 indexed requestId,
        address indexed sender,
        address indexed recipient,
        AssetType assetType,
        address assetAddress,
        uint256 amount,
        uint256 tokenId,
        bool isL3ToL2,
        uint256 timestamp
    );
    
    event BridgeRequestProcessed(
        uint256 indexed requestId,
        address indexed validator,
        BridgeStatus status,
        string l3TransactionHash,
        uint256 timestamp
    );
    
    event BridgeRequestCompleted(
        uint256 indexed requestId,
        address indexed recipient,
        uint256 amount,
        uint256 timestamp
    );
    
    event ValidatorRegistered(
        address indexed validator,
        uint256 stake,
        uint256 timestamp
    );
    
    event ValidatorStakeUpdated(
        address indexed validator,
        uint256 oldStake,
        uint256 newStake
    );
    
    event BridgeFeeUpdated(uint256 oldFee, uint256 newFee);
    event ValidatorStakeUpdated(uint256 oldStake, uint256 newStake);
    
    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================
    
    constructor(address _settlementContract) {
        require(_settlementContract != address(0), "Invalid settlement contract");
        settlementContract = _settlementContract;
        
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(BRIDGE_OPERATOR_ROLE, msg.sender);
        _grantRole(VALIDATOR_ROLE, msg.sender);
        _grantRole(EMERGENCY_ROLE, msg.sender);
    }
    
    // =============================================================================
    // BRIDGE FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Create a bridge request from L2 to L3
     * @param recipient Recipient address on L3
     * @param assetType Type of asset to bridge
     * @param assetAddress Asset contract address
     * @param amount Amount to bridge
     * @param tokenId Token ID (for NFTs)
     */
    function bridgeToL3(
        address recipient,
        AssetType assetType,
        address assetAddress,
        uint256 amount,
        uint256 tokenId
    ) external payable whenNotPaused nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(amount >= MIN_BRIDGE_AMOUNT, "Amount too small");
        require(amount <= MAX_BRIDGE_AMOUNT, "Amount too large");
        require(msg.value >= bridgeFee, "Insufficient bridge fee");
        
        requestCounter++;
        uint256 requestId = requestCounter;
        
        BridgeRequest storage request = bridgeRequests[requestId];
        request.requestId = requestId;
        request.sender = msg.sender;
        request.recipient = recipient;
        request.assetType = assetType;
        request.assetAddress = assetAddress;
        request.amount = amount;
        request.tokenId = tokenId;
        request.status = BridgeStatus.PENDING;
        request.timestamp = block.timestamp;
        request.deadline = block.timestamp + BRIDGE_TIMEOUT;
        request.isL3ToL2 = false;
        
        userRequests[msg.sender].push(requestId);
        totalRequests++;
        totalVolume += amount;
        
        // Transfer assets to bridge contract
        if (assetType == AssetType.TOKEN) {
            IERC20(assetAddress).transferFrom(msg.sender, address(this), amount);
        } else if (assetType == AssetType.NFT) {
            IERC721(assetAddress).transferFrom(msg.sender, address(this), tokenId);
        } else if (assetType == AssetType.ETH) {
            require(msg.value >= amount + bridgeFee, "Insufficient ETH");
        }
        
        emit BridgeRequestCreated(
            requestId,
            msg.sender,
            recipient,
            assetType,
            assetAddress,
            amount,
            tokenId,
            false,
            block.timestamp
        );
    }
    
    /**
     * @dev Process bridge request (L2 to L3)
     * @param requestId Request ID
     * @param l3TransactionHash L3 transaction hash
     * @param merkleRoot Merkle root for verification
     */
    function processL2ToL3Request(
        uint256 requestId,
        string memory l3TransactionHash,
        bytes32 merkleRoot
    ) external onlyRole(VALIDATOR_ROLE) whenNotPaused {
        BridgeRequest storage request = bridgeRequests[requestId];
        require(request.requestId != 0, "Request does not exist");
        require(request.status == BridgeStatus.PENDING, "Request not pending");
        require(!request.isL3ToL2, "Invalid request direction");
        require(block.timestamp <= request.deadline, "Request expired");
        
        request.status = BridgeStatus.PROCESSING;
        request.l3TransactionHash = l3TransactionHash;
        request.merkleRoot = merkleRoot;
        request.validator = msg.sender;
        
        // Update validator stats
        ValidatorInfo storage validator = validators[msg.sender];
        validator.totalProcessed++;
        validator.lastActivity = block.timestamp;
        
        emit BridgeRequestProcessed(
            requestId,
            msg.sender,
            BridgeStatus.PROCESSING,
            l3TransactionHash,
            block.timestamp
        );
    }
    
    /**
     * @dev Complete bridge request (L2 to L3)
     * @param requestId Request ID
     */
    function completeL2ToL3Request(uint256 requestId) external onlyRole(VALIDATOR_ROLE) whenNotPaused {
        BridgeRequest storage request = bridgeRequests[requestId];
        require(request.requestId != 0, "Request does not exist");
        require(request.status == BridgeStatus.PROCESSING, "Request not processing");
        require(!request.isL3ToL2, "Invalid request direction");
        
        request.status = BridgeStatus.COMPLETED;
        
        // Update validator stats
        ValidatorInfo storage validator = validators[msg.sender];
        validator.successRate = (validator.successRate * (validator.totalProcessed - 1) + 100) / validator.totalProcessed;
        
        emit BridgeRequestCompleted(
            requestId,
            request.recipient,
            request.amount,
            block.timestamp
        );
    }
    
    /**
     * @dev Process bridge request from L3 to L2
     * @param sender Sender address on L3
     * @param recipient Recipient address on L2
     * @param assetType Type of asset
     * @param assetAddress Asset contract address
     * @param amount Amount to bridge
     * @param tokenId Token ID (for NFTs)
     * @param l3TransactionHash L3 transaction hash
     * @param merkleRoot Merkle root for verification
     */
    function processL3ToL2Request(
        address sender,
        address recipient,
        AssetType assetType,
        address assetAddress,
        uint256 amount,
        uint256 tokenId,
        string memory l3TransactionHash,
        bytes32 merkleRoot
    ) external onlyRole(VALIDATOR_ROLE) whenNotPaused {
        require(sender != address(0), "Invalid sender");
        require(recipient != address(0), "Invalid recipient");
        require(amount >= MIN_BRIDGE_AMOUNT, "Amount too small");
        require(amount <= MAX_BRIDGE_AMOUNT, "Amount too large");
        require(bytes(l3TransactionHash).length > 0, "Invalid L3 transaction hash");
        require(!processedTransactions[keccak256(abi.encodePacked(l3TransactionHash))], "Transaction already processed");
        
        requestCounter++;
        uint256 requestId = requestCounter;
        
        BridgeRequest storage request = bridgeRequests[requestId];
        request.requestId = requestId;
        request.sender = sender;
        request.recipient = recipient;
        request.assetType = assetType;
        request.assetAddress = assetAddress;
        request.amount = amount;
        request.tokenId = tokenId;
        request.status = BridgeStatus.PROCESSING;
        request.timestamp = block.timestamp;
        request.deadline = block.timestamp + BRIDGE_TIMEOUT;
        request.l3TransactionHash = l3TransactionHash;
        request.merkleRoot = merkleRoot;
        request.validator = msg.sender;
        request.isL3ToL2 = true;
        
        userRequests[recipient].push(requestId);
        totalRequests++;
        totalVolume += amount;
        processedTransactions[keccak256(abi.encodePacked(l3TransactionHash))] = true;
        
        // Transfer assets to recipient
        if (assetType == AssetType.TOKEN) {
            IERC20(assetAddress).transfer(recipient, amount);
        } else if (assetType == AssetType.NFT) {
            IERC721(assetAddress).transferFrom(address(this), recipient, tokenId);
        } else if (assetType == AssetType.ETH) {
            payable(recipient).transfer(amount);
        }
        
        request.status = BridgeStatus.COMPLETED;
        
        // Update validator stats
        ValidatorInfo storage validator = validators[msg.sender];
        validator.totalProcessed++;
        validator.successRate = (validator.successRate * (validator.totalProcessed - 1) + 100) / validator.totalProcessed;
        validator.lastActivity = block.timestamp;
        
        emit BridgeRequestCreated(
            requestId,
            sender,
            recipient,
            assetType,
            assetAddress,
            amount,
            tokenId,
            true,
            block.timestamp
        );
        
        emit BridgeRequestCompleted(
            requestId,
            recipient,
            amount,
            block.timestamp
        );
    }
    
    // =============================================================================
    // VALIDATOR FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Register as a validator
     */
    function registerValidator() external whenNotPaused {
        require(!validators[msg.sender].isActive, "Already registered");
        
        // Transfer stake to contract
        IERC20(settlementContract).transferFrom(msg.sender, address(this), validatorStake);
        
        ValidatorInfo storage validator = validators[msg.sender];
        validator.isActive = true;
        validator.stake = validatorStake;
        validator.lastActivity = block.timestamp;
        
        _grantRole(VALIDATOR_ROLE, msg.sender);
        
        emit ValidatorRegistered(msg.sender, validatorStake, block.timestamp);
    }
    
    /**
     * @dev Unregister as a validator
     */
    function unregisterValidator() external whenNotPaused {
        ValidatorInfo storage validator = validators[msg.sender];
        require(validator.isActive, "Not registered");
        
        validator.isActive = false;
        
        // Return stake to validator
        IERC20(settlementContract).transfer(msg.sender, validator.stake);
        
        _revokeRole(VALIDATOR_ROLE, msg.sender);
        
        emit ValidatorStakeUpdated(msg.sender, validator.stake, 0);
    }
    
    // =============================================================================
    // VIEW FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Get bridge request details
     * @param requestId Request ID
     * @return id Request ID
     * @return sender Sender address
     * @return recipient Recipient address
     * @return assetType Asset type
     * @return assetAddress Asset address
     * @return amount Amount
     * @return tokenId Token ID
     * @return status Request status
     * @return timestamp Timestamp
     * @return deadline Deadline
     * @return merkleRoot Merkle root
     * @return l3TransactionHash L3 transaction hash
     * @return validator Validator address
     * @return isL3ToL2 Whether L3 to L2
     */
    function getBridgeRequest(uint256 requestId) external view returns (
        uint256 id,
        address sender,
        address recipient,
        AssetType assetType,
        address assetAddress,
        uint256 amount,
        uint256 tokenId,
        BridgeStatus status,
        uint256 timestamp,
        uint256 deadline,
        bytes32 merkleRoot,
        string memory l3TransactionHash,
        address validator,
        bool isL3ToL2
    ) {
        BridgeRequest storage request = bridgeRequests[requestId];
        return (
            request.requestId,
            request.sender,
            request.recipient,
            request.assetType,
            request.assetAddress,
            request.amount,
            request.tokenId,
            request.status,
            request.timestamp,
            request.deadline,
            request.merkleRoot,
            request.l3TransactionHash,
            request.validator,
            request.isL3ToL2
        );
    }
    
    /**
     * @dev Get validator information
     * @param validator Validator address
     * @return isActive Whether active
     * @return totalProcessed Total processed
     * @return successRate Success rate
     * @return lastActivity Last activity
     * @return stake Stake amount
     */
    function getValidatorInfo(address validator) external view returns (
        bool isActive,
        uint256 totalProcessed,
        uint256 successRate,
        uint256 lastActivity,
        uint256 stake
    ) {
        ValidatorInfo storage info = validators[validator];
        return (
            info.isActive,
            info.totalProcessed,
            info.successRate,
            info.lastActivity,
            info.stake
        );
    }
    
    /**
     * @dev Get user requests
     * @param user User address
     * @return requestIds Array of request IDs
     */
    function getUserRequests(address user) external view returns (uint256[] memory) {
        return userRequests[user];
    }
    
    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Update bridge fee
     * @param newFee New fee amount
     */
    function updateBridgeFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldFee = bridgeFee;
        bridgeFee = newFee;
        emit BridgeFeeUpdated(oldFee, newFee);
    }
    
    /**
     * @dev Update validator stake requirement
     * @param newStake New stake amount
     */
    function updateValidatorStake(uint256 newStake) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldStake = validatorStake;
        validatorStake = newStake;
        emit ValidatorStakeUpdated(oldStake, newStake);
    }
    
    /**
     * @dev Update settlement contract
     * @param newSettlement New settlement contract address
     */
    function updateSettlementContract(address newSettlement) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newSettlement != address(0), "Invalid settlement contract");
        settlementContract = newSettlement;
    }
    
    /**
     * @dev Pause bridge
     */
    function pause() external onlyRole(EMERGENCY_ROLE) {
        _pause();
    }
    
    /**
     * @dev Unpause bridge
     */
    function unpause() external onlyRole(EMERGENCY_ROLE) {
        _unpause();
    }
    
    /**
     * @dev Emergency withdraw tokens
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdrawTokens(address token, uint256 amount) external onlyRole(EMERGENCY_ROLE) {
        IERC20(token).transfer(msg.sender, amount);
    }
    
    /**
     * @dev Emergency withdraw ETH
     */
    function emergencyWithdrawETH() external onlyRole(EMERGENCY_ROLE) {
        payable(msg.sender).transfer(address(this).balance);
    }
    
    // =============================================================================
    // RECEIVE FUNCTION
    // =============================================================================
    
    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {}
}

contracts/GameDinSettlement.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title GameDin Settlement Contract
 * @dev L2 settlement layer for GameDin L3 gaming blockchain
 * Handles cross-chain asset transfers, dispute resolution, and final settlement
 */
contract GameDinSettlement is ERC20, ERC20Pausable, AccessControl, ReentrancyGuard {
    // =============================================================================
    // CONSTANTS & ROLES
    // =============================================================================
    
    bytes32 public constant SETTLEMENT_ROLE = keccak256("SETTLEMENT_ROLE");
    bytes32 public constant BRIDGE_ROLE = keccak256("BRIDGE_ROLE");
    bytes32 public constant DISPUTE_RESOLVER_ROLE = keccak256("DISPUTE_RESOLVER_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");
    
    uint256 public constant SETTLEMENT_TIMEOUT = 7 days;
    uint256 public constant DISPUTE_WINDOW = 3 days;
    uint256 public constant MAX_SETTLEMENT_AMOUNT = 1000000 * 10**18; // 1M tokens
    
    // =============================================================================
    // STRUCTS & ENUMS
    // =============================================================================
    
    enum SettlementStatus {
        PENDING,
        CONFIRMED,
        DISPUTED,
        RESOLVED,
        CANCELLED
    }
    
    enum DisputeReason {
        INSUFFICIENT_FUNDS,
        INVALID_TRANSACTION,
        FRAUD_DETECTED,
        TECHNICAL_ERROR,
        OTHER
    }
    
    struct Settlement {
        uint256 settlementId;
        address from;
        address to;
        uint256 amount;
        uint256 timestamp;
        SettlementStatus status;
        string l3TransactionHash;
        bytes32 merkleRoot;
        uint256 disputeDeadline;
        address disputeInitiator;
        DisputeReason disputeReason;
        string disputeDetails;
    }
    
    struct Dispute {
        uint256 settlementId;
        address initiator;
        DisputeReason reason;
        string details;
        uint256 timestamp;
        bool resolved;
        address resolver;
        string resolution;
    }
    
    struct BridgeConfig {
        address bridgeAddress;
        bool isActive;
        uint256 minConfirmations;
        uint256 maxAmount;
        uint256 fee;
    }
    
    // =============================================================================
    // STATE VARIABLES
    // =============================================================================
    
    uint256 public settlementCounter;
    uint256 public totalSettlements;
    uint256 public totalDisputes;
    uint256 public settlementFee = 0.001 * 10**18; // 0.001 tokens
    uint256 public disputeFee = 0.01 * 10**18; // 0.01 tokens
    
    mapping(uint256 => Settlement) public settlements;
    mapping(uint256 => Dispute) public disputes;
    mapping(address => uint256[]) public userSettlements;
    mapping(bytes32 => bool) public processedL3Transactions;
    mapping(address => BridgeConfig) public bridgeConfigs;
    
    // =============================================================================
    // EVENTS
    // =============================================================================
    
    event SettlementCreated(
        uint256 indexed settlementId,
        address indexed from,
        address indexed to,
        uint256 amount,
        string l3TransactionHash,
        uint256 timestamp
    );
    
    event SettlementConfirmed(
        uint256 indexed settlementId,
        address indexed confirmer,
        uint256 timestamp
    );
    
    event SettlementDisputed(
        uint256 indexed settlementId,
        address indexed initiator,
        DisputeReason reason,
        string details,
        uint256 timestamp
    );
    
    event DisputeResolved(
        uint256 indexed settlementId,
        address indexed resolver,
        string resolution,
        uint256 timestamp
    );
    
    event BridgeConfigUpdated(
        address indexed bridgeAddress,
        bool isActive,
        uint256 minConfirmations,
        uint256 maxAmount,
        uint256 fee
    );
    
    event SettlementFeeUpdated(uint256 oldFee, uint256 newFee);
    event DisputeFeeUpdated(uint256 oldFee, uint256 newFee);
    
    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================
    
    constructor(
        address initialOwner,
        uint256 initialSupply
    ) ERC20("GameDin Settlement Token", "GDIS") {
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(SETTLEMENT_ROLE, initialOwner);
        _grantRole(BRIDGE_ROLE, initialOwner);
        _grantRole(DISPUTE_RESOLVER_ROLE, initialOwner);
        _grantRole(EMERGENCY_ROLE, initialOwner);
        
        _mint(initialOwner, initialSupply);
    }
    
    // =============================================================================
    // SETTLEMENT FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Create a new settlement
     * @param to Recipient address
     * @param amount Settlement amount
     * @param l3TransactionHash L3 transaction hash
     * @param merkleRoot Merkle root for verification
     */
    function createSettlement(
        address to,
        uint256 amount,
        string memory l3TransactionHash,
        bytes32 merkleRoot
    ) external onlyRole(SETTLEMENT_ROLE) whenNotPaused nonReentrant {
        require(to != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");
        require(amount <= MAX_SETTLEMENT_AMOUNT, "Amount exceeds maximum");
        require(bytes(l3TransactionHash).length > 0, "Invalid L3 transaction hash");
        require(!processedL3Transactions[keccak256(abi.encodePacked(l3TransactionHash))], "L3 transaction already processed");
        
        settlementCounter++;
        uint256 settlementId = settlementCounter;
        
        Settlement storage settlement = settlements[settlementId];
        settlement.settlementId = settlementId;
        settlement.from = msg.sender;
        settlement.to = to;
        settlement.amount = amount;
        settlement.timestamp = block.timestamp;
        settlement.status = SettlementStatus.PENDING;
        settlement.l3TransactionHash = l3TransactionHash;
        settlement.merkleRoot = merkleRoot;
        settlement.disputeDeadline = block.timestamp + DISPUTE_WINDOW;
        
        userSettlements[to].push(settlementId);
        processedL3Transactions[keccak256(abi.encodePacked(l3TransactionHash))] = true;
        totalSettlements++;
        
        emit SettlementCreated(settlementId, msg.sender, to, amount, l3TransactionHash, block.timestamp);
    }
    
    /**
     * @dev Confirm a settlement
     * @param settlementId Settlement ID
     */
    function confirmSettlement(uint256 settlementId) external onlyRole(SETTLEMENT_ROLE) whenNotPaused {
        Settlement storage settlement = settlements[settlementId];
        require(settlement.settlementId != 0, "Settlement does not exist");
        require(settlement.status == SettlementStatus.PENDING, "Settlement not pending");
        require(block.timestamp <= settlement.disputeDeadline, "Dispute window expired");
        
        settlement.status = SettlementStatus.CONFIRMED;
        
        // Transfer tokens to recipient
        _transfer(settlement.from, settlement.to, settlement.amount);
        
        emit SettlementConfirmed(settlementId, msg.sender, block.timestamp);
    }
    
    /**
     * @dev Initiate a dispute for a settlement
     * @param settlementId Settlement ID
     * @param reason Dispute reason
     * @param details Dispute details
     */
    function initiateDispute(
        uint256 settlementId,
        DisputeReason reason,
        string memory details
    ) external payable whenNotPaused {
        require(msg.value >= disputeFee, "Insufficient dispute fee");
        
        Settlement storage settlement = settlements[settlementId];
        require(settlement.settlementId != 0, "Settlement does not exist");
        require(settlement.status == SettlementStatus.PENDING, "Settlement not pending");
        require(block.timestamp <= settlement.disputeDeadline, "Dispute window expired");
        require(msg.sender == settlement.to || hasRole(DISPUTE_RESOLVER_ROLE, msg.sender), "Not authorized to dispute");
        
        settlement.status = SettlementStatus.DISPUTED;
        settlement.disputeInitiator = msg.sender;
        settlement.disputeReason = reason;
        settlement.disputeDetails = details;
        
        totalDisputes++;
        
        Dispute storage dispute = disputes[settlementId];
        dispute.settlementId = settlementId;
        dispute.initiator = msg.sender;
        dispute.reason = reason;
        dispute.details = details;
        dispute.timestamp = block.timestamp;
        
        emit SettlementDisputed(settlementId, msg.sender, reason, details, block.timestamp);
    }
    
    /**
     * @dev Resolve a dispute
     * @param settlementId Settlement ID
     * @param resolution Resolution details
     * @param approveSettlement Whether to approve the settlement
     */
    function resolveDispute(
        uint256 settlementId,
        string memory resolution,
        bool approveSettlement
    ) external onlyRole(DISPUTE_RESOLVER_ROLE) whenNotPaused {
        Settlement storage settlement = settlements[settlementId];
        Dispute storage dispute = disputes[settlementId];
        
        require(settlement.settlementId != 0, "Settlement does not exist");
        require(settlement.status == SettlementStatus.DISPUTED, "Settlement not disputed");
        require(!dispute.resolved, "Dispute already resolved");
        
        dispute.resolved = true;
        dispute.resolver = msg.sender;
        dispute.resolution = resolution;
        
        if (approveSettlement) {
            settlement.status = SettlementStatus.RESOLVED;
            _transfer(settlement.from, settlement.to, settlement.amount);
        } else {
            settlement.status = SettlementStatus.CANCELLED;
        }
        
        emit DisputeResolved(settlementId, msg.sender, resolution, block.timestamp);
    }
    
    // =============================================================================
    // BRIDGE FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Configure a bridge
     * @param bridgeAddress Bridge contract address
     * @param isActive Whether bridge is active
     * @param minConfirmations Minimum confirmations required
     * @param maxAmount Maximum amount per transaction
     * @param fee Bridge fee
     */
    function configureBridge(
        address bridgeAddress,
        bool isActive,
        uint256 minConfirmations,
        uint256 maxAmount,
        uint256 fee
    ) external onlyRole(BRIDGE_ROLE) {
        require(bridgeAddress != address(0), "Invalid bridge address");
        
        BridgeConfig storage config = bridgeConfigs[bridgeAddress];
        config.bridgeAddress = bridgeAddress;
        config.isActive = isActive;
        config.minConfirmations = minConfirmations;
        config.maxAmount = maxAmount;
        config.fee = fee;
        
        emit BridgeConfigUpdated(bridgeAddress, isActive, minConfirmations, maxAmount, fee);
    }
    
    /**
     * @dev Process bridge transaction
     * @param from Source address
     * @param to Destination address
     * @param amount Amount to transfer
     * @param bridgeAddress Bridge address
     */
    function processBridgeTransaction(
        address from,
        address to,
        uint256 amount,
        address bridgeAddress
    ) external onlyRole(BRIDGE_ROLE) whenNotPaused {
        BridgeConfig storage config = bridgeConfigs[bridgeAddress];
        require(config.isActive, "Bridge not active");
        require(amount <= config.maxAmount, "Amount exceeds bridge limit");
        
        _transfer(from, to, amount);
    }
    
    // =============================================================================
    // VIEW FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Get settlement details
     * @param settlementId Settlement ID
     * @return id Settlement ID
     * @return from Sender address
     * @return to Recipient address
     * @return amount Amount
     * @return timestamp Timestamp
     * @return status Settlement status
     * @return l3TransactionHash L3 transaction hash
     * @return merkleRoot Merkle root
     * @return disputeDeadline Dispute deadline
     * @return disputeInitiator Dispute initiator
     * @return disputeReason Dispute reason
     * @return disputeDetails Dispute details
     */
    function getSettlement(uint256 settlementId) external view returns (
        uint256 id,
        address from,
        address to,
        uint256 amount,
        uint256 timestamp,
        SettlementStatus status,
        string memory l3TransactionHash,
        bytes32 merkleRoot,
        uint256 disputeDeadline,
        address disputeInitiator,
        DisputeReason disputeReason,
        string memory disputeDetails
    ) {
        Settlement storage settlement = settlements[settlementId];
        return (
            settlement.settlementId,
            settlement.from,
            settlement.to,
            settlement.amount,
            settlement.timestamp,
            settlement.status,
            settlement.l3TransactionHash,
            settlement.merkleRoot,
            settlement.disputeDeadline,
            settlement.disputeInitiator,
            settlement.disputeReason,
            settlement.disputeDetails
        );
    }
    
    /**
     * @dev Get dispute details
     * @param settlementId Settlement ID
     * @return id Settlement ID
     * @return initiator Dispute initiator
     * @return reason Dispute reason
     * @return details Dispute details
     * @return timestamp Timestamp
     * @return resolved Whether resolved
     * @return resolver Resolver address
     * @return resolution Resolution details
     */
    function getDispute(uint256 settlementId) external view returns (
        uint256 id,
        address initiator,
        DisputeReason reason,
        string memory details,
        uint256 timestamp,
        bool resolved,
        address resolver,
        string memory resolution
    ) {
        Dispute storage dispute = disputes[settlementId];
        return (
            dispute.settlementId,
            dispute.initiator,
            dispute.reason,
            dispute.details,
            dispute.timestamp,
            dispute.resolved,
            dispute.resolver,
            dispute.resolution
        );
    }
    
    /**
     * @dev Get user settlements
     * @param user User address
     * @return settlementIds Array of settlement IDs
     */
    function getUserSettlements(address user) external view returns (uint256[] memory) {
        return userSettlements[user];
    }
    
    /**
     * @dev Get bridge configuration
     * @param bridgeAddress Bridge address
     * @return bridge Bridge address
     * @return isActive Whether active
     * @return minConfirmations Minimum confirmations
     * @return maxAmount Maximum amount
     * @return fee Bridge fee
     */
    function getBridgeConfig(address bridgeAddress) external view returns (
        address bridge,
        bool isActive,
        uint256 minConfirmations,
        uint256 maxAmount,
        uint256 fee
    ) {
        BridgeConfig storage config = bridgeConfigs[bridgeAddress];
        return (
            config.bridgeAddress,
            config.isActive,
            config.minConfirmations,
            config.maxAmount,
            config.fee
        );
    }
    
    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Update settlement fee
     * @param newFee New fee amount
     */
    function updateSettlementFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldFee = settlementFee;
        settlementFee = newFee;
        emit SettlementFeeUpdated(oldFee, newFee);
    }
    
    /**
     * @dev Update dispute fee
     * @param newFee New fee amount
     */
    function updateDisputeFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldFee = disputeFee;
        disputeFee = newFee;
        emit DisputeFeeUpdated(oldFee, newFee);
    }
    
    /**
     * @dev Pause contract
     */
    function pause() external onlyRole(EMERGENCY_ROLE) {
        _pause();
    }
    
    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(EMERGENCY_ROLE) {
        _unpause();
    }
    
    /**
     * @dev Emergency withdraw ETH
     */
    function emergencyWithdrawETH() external onlyRole(EMERGENCY_ROLE) {
        payable(msg.sender).transfer(address(this).balance);
    }
    
    /**
     * @dev Emergency withdraw tokens
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdrawTokens(address token, uint256 amount) external onlyRole(EMERGENCY_ROLE) {
        IERC20(token).transfer(msg.sender, amount);
    }
    
    // =============================================================================
    // OVERRIDE FUNCTIONS
    // =============================================================================
    
    /**
     * @dev Override transfer function to check pause
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20, ERC20Pausable) whenNotPaused {
        super._update(from, to, amount);
    }
    
    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {}
}

